// File: EmployeeDashboard.js
import React, { useEffect, useState } from 'react';
import {
  checkin, checkout, getHistory,
  getLeaveHistory, submitLeaveRequest,
  getEmployeeSummary, getProfile,
  updateEmployeeProfile
} from '../services/api';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './EmployeeDashboard.css';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const Sidebar = ({ activeTab, setActiveTab, handleLogout }) => (
  <aside className="sidebar">
    <div className="sidebar-logo">Employee</div>
    <ul>
      <li className={activeTab === 'dashboard' ? 'active' : ''} onClick={() => setActiveTab('dashboard')}>Dashboard</li>
      <li className={activeTab === 'attendance' ? 'active' : ''} onClick={() => setActiveTab('attendance')}>Attendance</li>
      <li className={activeTab === 'holiday' ? 'active' : ''} onClick={() => setActiveTab('holiday')}>Holiday History</li>

      <li className={activeTab === 'leave' ? 'active' : ''} onClick={() => setActiveTab('leave')}>Leave Request</li>
      <li className={activeTab === 'holiday' ? 'active' : ''} onClick={() => setActiveTab('holiday')}>Holiday Calendar</li>
      <li className={activeTab === 'holiday' ? 'active' : ''} onClick={() => setActiveTab('holiday')}>Holiday Calendar</li>

      <li onClick={handleLogout}>Logout</li>
    </ul>
  </aside>
);

const Topbar = () => (
  <header className="topbar">
    <div className="top-right">
      <input type="text" placeholder="Search here" className="search-right" />
      <span>üîî</span>
      <span>‚öôÔ∏è</span>
      <span>üë§</span>
    </div>
  </header>
);

const SummaryCards = ({ summary, leavesLeft, nextHoliday }) => (
  <div className="summary-cards">
    <div className="card"><h4>Upcoming Holiday</h4><p>{nextHoliday?.name || '‚Äî'}</p><small>{nextHoliday?.date || ''}</small></div>
    <div className="card"><h4>Leaves Taken</h4><p>{summary.leavesTaken}</p></div>
    <div className="card"><h4>Leaves Left</h4><p>{leavesLeft}</p></div>
    <div className="card"><h4>Pending Requests</h4><p>{summary.pendingRequests}</p></div>
  </div>
);

const DashboardTab = () => {
  const data = {
    labels: ['Present', 'Absent'],
    datasets: [
      {
        label: 'Attendance',
        data: [85, 15],
        backgroundColor: ['#4caf50', '#f44336'],
        borderWidth: 0
      }
    ]
  };
  return (
    <>
      <div className="card">
        <h3>Employee Activity</h3>
        <div className="small-chart">
          <Pie data={data} />
        </div>
      </div>
      <div className="card">
        <h3>Attendance History</h3>
        <table>
          <thead><tr><th>Date</th><th>Check-in</th><th>Check-out</th></tr></thead>
          <tbody>
            <tr><td>2025-05-16</td><td>09:00</td><td>17:00</td></tr>
            <tr><td>2025-05-15</td><td>09:10</td><td>17:05</td></tr>
          </tbody>
        </table>
      </div>
    </>
  );
};

const AttendanceTab = () => (
  <div className="card">
    <div className="btn-group">
      <button>Check In</button>
      <button>Check Out</button>
    </div>
  </div>
);

const LeaveTab = () => <div className="card">[Leave Form & History]</div>;
const HolidayTab = () => (
  <div className="card">
    <h3>Holiday Calendar</h3>
    <ul>
      <li>May Day - 2025-05-01</li>
      <li>Independence Day - 2025-08-15</li>
      <li>Ganesh Chaturthi - 2025-08-27</li>
      <li>Gandhi Jayanti - 2025-10-02</li>
