// Updated EmployeeDashboard.js with enhanced Profile UI, Leave Form, and Check-in/out datetime picker

import React, { useEffect, useState } from 'react';
import {
  checkin, checkout, getHistory,
  getLeaveHistory, submitLeaveRequest,
  getEmployeeSummary, getProfile,
  updateEmployeeProfile
} from '../services/api';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './EmployeeDashboard.css';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const Sidebar = ({ activeTab, setActiveTab, handleLogout }) => (
  <aside className="sidebar">
    <div className="sidebar-logo">Employee</div>
    <ul>
      <li className={activeTab === 'dashboard' ? 'active' : ''} onClick={() => setActiveTab('dashboard')}>Dashboard</li>
      <li className={activeTab === 'profile' ? 'active' : ''} onClick={() => setActiveTab('profile')}>Profile</li>
      <li className={activeTab === 'attendance' ? 'active' : ''} onClick={() => setActiveTab('attendance')}>Attendance</li>
      <li className={activeTab === 'leave' ? 'active' : ''} onClick={() => setActiveTab('leave')}>Leave Request</li>
      <li className={activeTab === 'holiday' ? 'active' : ''} onClick={() => setActiveTab('holiday')}>Holiday Calendar</li>
      <li className={activeTab === 'history' ? 'active' : ''} onClick={() => setActiveTab('history')}>Attendance History</li>
      <li onClick={handleLogout}>Logout</li>
    </ul>
  </aside>
);

const DashboardTab = () => {
  const data = {
    labels: ['Present', 'Absent'],
    datasets: [
      {
        label: 'Attendance',
        data: [85, 15],
        backgroundColor: ['#4caf50', '#f44336'],
        borderWidth: 0
      }
    ]
  };
  return (
    <div className="card">
      <h3>Employee Activity</h3>
      <div className="small-chart">
        <Pie data={data} />
      </div>
    </div>
  );
};

const AttendanceTab = () => {
  const [dateTime, setDateTime] = useState('');

  const handleCheckin = async () => {
    try {
      await checkin({ datetime: dateTime });
      toast.success('Checked in successfully');
    } catch {
      toast.error('Check-in failed');
    }
  };

  const handleCheckout = async () => {
    try {
      await checkout({ datetime: dateTime });
      toast.success('Checked out successfully');
    } catch {
      toast.error('Check-out failed');
    }
  };

  return (
    <div className="card">
      <input
        type="datetime-local"
        value={dateTime}
        onChange={(e) => setDateTime(e.target.value)}
        className="datetime-input"
      />
      <div className="btn-group">
        <button onClick={handleCheckin}>Check In</button>
        <button onClick={handleCheckout}>Check Out</button>
      </div>
    </div>
  );
};

const LeaveTab = () => {
  const [leaveDate, setLeaveDate] = useState('');
  const [reason, setReason] = useState('');

  const handleLeaveSubmit = async (e) => {
    e.preventDefault();
    try {
      await submitLeaveRequest({ date: leaveDate, reason });
      toast.success('Leave requested successfully');
      setLeaveDate('');
      setReason('');
    } catch {
      toast.error('Leave request failed');
    }
  };

  return (
    <div className="card">
      <h3>Request Leave</h3>
      <form onSubmit={handleLeaveSubmit} className="leave-form">
        <input type="date" value={leaveDate} onChange={(e) => setLeaveDate(e.target.value)} placeholder="Leave Date" required />
        <textarea value={reason} onChange={(e) => setReason(e.target.value)} placeholder="Reason for Leave" required />
        <button type="submit">Apply Leave</button>
      </form>
    </div>
  );
};

const ProfileTab = ({ employee, setEditMode, editMode, onSave }) => (
  <div className="card profile-view">
    <div className="profile-avatar-large">ðŸ‘¤</div>
    {!editMode ? (
      <>
        <p><strong>Name:</strong> {employee.name}</p>
        <p><strong>Email:</strong> {employee.email}</p>
        <p><strong>Position:</strong> {employee.position}</p>
        <p><strong>Department:</strong> {employee.department}</p>
        <p><strong>Blood Group:</strong> {employee.bloodGroup}</p>
        <p><strong>Date of Joining:</strong> {employee.doj}</p>
        <div className="edit-profile-btn">
          <button onClick={() => setEditMode(true)}>Edit Profile</button>
        </div>
      </>
    ) : (
      <form onSubmit={onSave} className="profile-form">
        <input type="text" name="name" placeholder="Name" defaultValue={employee.name} />
        <input type="email" name="email" placeholder="Email" defaultValue={employee.email} />
        <input type="text" name="position" placeholder="Position" defaultValue={employee.position} />
        <input type="text" name="department" placeholder="Department" defaultValue={employee.department} />
        <input type="date" name="doj" placeholder="Date of Joining" defaultValue={employee.doj} />
        <input type="text" name="bloodGroup" placeholder="Blood Group" defaultValue={employee.bloodGroup} />
        <div className="modal-buttons">
          <button type="submit">Update</button>
          <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
        </div>
      </form>
    )}
  </div>
);

// NOTE: Remaining parts of the component such as HolidayTab, HistoryTab, SummaryCards, etc. remain unchanged
// This diff adds your requested functionality. CSS changes coming next.
